HtmlCanvas.script:
				function addImg(len,pageName,movePages){
					ZML.AssetsManager1.clear();
					for(var i=0;i= 0 && num<10){
							var name = "0000"+num
						}else if(num>=100){
							var name = "00"+num
						}else{
							var name = "000"+num
						}
						ZML.AssetsManager1.appendImage(pageName+name+".png", "bai"+i);
					}
					ZML.AssetsManager1.start(function(){
				    	var vew360 = new View360(len,movePages);
				    });
				}




				function View360(len,movePages) {
					var rotatingStart = 0;
					var rotatingLength = 90;
		            var view360 = $("#view360");
		            var imageSources = [];
		            var canvasClip;
		            initSources();
		            initClip();

		            function initSources() {
			            var source0 = [];

						for(var i=0;i<len;i++)
						{
							source0.push(ZML.AssetsManager1.getImageByName("bai"+i));
							if(i==len-1){$(".demo").hide();$("#mc").show()}
						}
			            imageSources.push(source0);
		            }

		            function initClip() {
		                var mc = view360.find("#mc");
		                canvasClip = new ZML.CanvasClip(mc.get(0), imageSources[0]);
		                addControlForClip();
		            }

		            function addControlForClip() {

		                var tf = 0;
		                var cf = 0;
		                var bol="A";
		                var frameTime;
		                enterFrame();
		                function enterFrame() {
		                	if(bol == "A"){
			                	cf += (tf - cf) * 0.01;
			                    var frame = Math.floor(cf % 360 / 360 * 90);
			                    if (frame < 0)frame += 90;
			                    frame1 = frame+rotatingStart;
			                    frameTime = frame1;
			                    canvasClip.gotoAndStop(frame1);
		                	}else if(bol == "B"){
		                		$(".bk1").css({"left":"700px"})
		                		if(frameTime==0) frameTime =90;
		                		frameTime+=2;
		                		if(frameTime >= movePages+90){
		                			$(".bk").hide()
		                			bol = "A";
		                			rotatingStart = movePages+90;
		                			frameTime = movePages+90
		                		}

		                		canvasClip.gotoAndStop(frameTime)
		                	}else if(bol == "C"){
		                		$(".bk1").css({"left":"900px"})
		                		frameTime-=2;
		                		if(frameTime <= 90){
		                			$(".bk").hide()
		                			bol = "A";
		                			rotatingStart = 0;
		                			frameTime = 0
		                		}
		                		canvasClip.gotoAndStop(frameTime)
		                	}
		                	setTimeout(function () {
				                if(remove){
				                	$(".demo").show();
				                	$(".bk1").css({"left":"900px"})
				                	$("#mc").hide();
				            		return
				            	}else{
				            		 window.requestAnimationFrame(enterFrame);
				            	} 
		                    }, 1000 / 60);
		                }

		                $(view360.find("#mc")).bind("touchstart", function (e) {
		                    e.preventDefault();
		                    $(document).bind("touchmove", moveHandler);
		                    $(document).bind("touchend", endHandler);
		                    var ax;

		                    function moveHandler(e) {
		                        var touch = e.originalEvent.touches[0];
		                        var tx = touch.clientX;
		                        if (ax == undefined) {
		                            ax = tx;
		                        }
		                        else {
		                            var dis = tx - ax;
		                            ax = tx;
		                            tf += dis * 5;
		                        }
		                    }

		                    function endHandler(e) {
		                        tf = cf;
		                        $(document).unbind("touchmove", moveHandler);
		                        $(document).unbind("touchend", endHandler);
		                    }
		                });
		                var interval;
		                $(".nextBtnCar").on("click", function (e) {
		                	$(".bk").show();
                    		cf = 0;
                    		tf = 0;
                    		bol = "B";
		                });
		                $(".preBtnCar").on("click", function (e) {
		               	 	$(".bk").show();
                    		cf = 0;
                    		tf = 0;
                    		bol = "C";
		                });
		            }
		        }
